import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:stroll/features/navigation/widget/bottom_nav.dart';import 'package:stroll/features/task/task.dart';import 'package:stroll/shared/shared.dart';class MainScreen extends StatelessWidget {  MainScreen({Key? key}) : super(key: key);  final _bottomNavNotifier = ValueNotifier(0);  @override  Widget build(BuildContext context) {    final bottomNavPages = [      TaskScreen(),      0.verticalSpace,      0.verticalSpace,      0.verticalSpace,    ];    return ValueListenableBuilder(        valueListenable: _bottomNavNotifier,        builder: (context, currentPage, __) {          return PageWrapper(            bottomNavigationBar: StrollBottomNav(              navIndex: currentPage,              onItemTap: (page) => _bottomNavNotifier.value = page,            ),            backgroundColor: StrollColors.white,            child: Stack(              children: [                AnimatedSwitcher(                  duration: const Duration(milliseconds: 300),                  switchInCurve: Curves.easeIn,                  switchOutCurve: Curves.easeOut,                  child: bottomNavPages.elementAt(currentPage),                ),              ],            ),          );        });  }}