import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:flutter_svg/svg.dart';import 'package:stroll/shared/shared.dart';class StrollBottomNav extends StatelessWidget {  const StrollBottomNav({super.key, required this.onItemTap, required this.navIndex});  final int navIndex;  final void Function(int) onItemTap;  @override  Widget build(BuildContext context) {    return Container(      padding: REdgeInsets.only(bottom: MediaQuery.of(context).padding.bottom),      decoration: const BoxDecoration(        color: StrollColors.grey800,      ),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceAround,        children: [          NavItem(            onPressed: () => onItemTap(0),            isActive: navIndex == 0,            asset: 'assets/icons/card.svg',          ),          NavItem(            onPressed: () => onItemTap(1),            isActive: navIndex == 1,            asset: 'assets/icons/bonfire.svg',          ),          NavItem(            onPressed: () => onItemTap(2),            isActive: navIndex == 2,            badgeCount: 10,            asset: 'assets/icons/chat_bubble.svg',          ),          NavItem(            onPressed: () => onItemTap(3),            isActive: navIndex == 3,            asset: 'assets/icons/user.svg',          ),        ],      ),    );  }}class NavItem extends StatelessWidget {  const NavItem({    super.key,    required this.isActive,    required this.onPressed,    required this.asset,    this.badgeCount,  });  final bool isActive;  final void Function() onPressed;  final int? badgeCount;  final String asset;  @override  Widget build(BuildContext context) {    return InkWell(      onTap: onPressed,      child: SizedBox(        height: 45.h,        child: Stack(          children: [            SvgPicture.asset(              asset,              width: 44.h,              colorFilter:                  !isActive ? null : const ColorFilter.mode(StrollColors.grey400, BlendMode.srcIn),            ),            if (badgeCount != null)              Positioned(                top: 8.h,                right: 0,                child: Container(                  padding: REdgeInsets.symmetric(horizontal: 4).copyWith(top: 2),                  decoration: BoxDecoration(                      color: StrollColors.primaryPurple,                      borderRadius: BorderRadius.circular(15.r),                      border: Border.all(color: StrollColors.grey800, width: 2.w)),                  child: Text(                    '$badgeCount',                    style: StrollFont.w700.copyWith(                      color: StrollColors.grey800,                      fontSize: 7.sp,                      height: 8.54.h / 7.sp,                    ),                  ),                ),              )          ],        ),      ),    );  }}