import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:flutter_svg/svg.dart';import 'package:stroll/shared/shared.dart';class StrollBottomNav extends StatelessWidget {  const StrollBottomNav({super.key, required this.onItemTap, required this.navIndex});  final int navIndex;  final void Function(int) onItemTap;  @override  Widget build(BuildContext context) {    return Container(      padding: REdgeInsets.only(bottom: MediaQuery.of(context).padding.bottom),      decoration: BoxDecoration(        color: StrollColors.grey800,        boxShadow: [          BoxShadow(            color: StrollColors.grey500.withOpacity(0.1),            blurRadius: 16,            offset: const Offset(0, -12),          )        ],      ),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceAround,        children: [          NavItem(            onPressed: () => onItemTap(0),            isActive: navIndex == 0,            asset: 'assets/icons/card.svg',          ),          NavItem(            onPressed: () => onItemTap(1),            isActive: navIndex == 1,            asset: 'assets/icons/bonfire.svg',          ),          NavItem(            onPressed: () => onItemTap(2),            isActive: navIndex == 2,            asset: 'assets/icons/chat_bubble.svg',          ),          NavItem(            onPressed: () => onItemTap(3),            isActive: navIndex == 3,            asset: 'assets/icons/user.svg',          ),        ],      ),    );  }}class NavItem extends StatelessWidget {  const NavItem({    super.key,    required this.isActive,    required this.onPressed,    required this.asset,  });  final bool isActive;  final void Function() onPressed;  final String asset;  @override  Widget build(BuildContext context) {    return InkWell(      onTap: onPressed,      child: SvgPicture.asset(        asset,        width: 44.h,        colorFilter:            !isActive ? null : const ColorFilter.mode(StrollColors.grey400, BlendMode.srcIn),      ),    );  }}