import 'package:animate_do/animate_do.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:stroll/core/core.dart';import 'package:stroll/features/navigation/view/main_screen.dart';import 'package:stroll/features/task/task.dart';import 'package:stroll/shared/shared.dart';class SplashScreen extends StatefulWidget {  const SplashScreen({Key? key}) : super(key: key);  @override  State<SplashScreen> createState() => _SplashScreenState();}class _SplashScreenState extends State<SplashScreen> with SingleTickerProviderStateMixin {  late AnimationController _controller;  late Animation<double> _animation;  @override  void initState() {    _controller = AnimationController(      duration: const Duration(seconds: 2),      vsync: this,    )..repeat(reverse: true);    _animation = CurvedAnimation(      parent: _controller,      curve: Curves.easeInOut,    );    Future.delayed(const Duration(seconds: 3), () {      if (mounted) {        _controller.stop();        context.pushReplacement(MainScreen());      }    });    super.initState();  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return PageWrapper(      backgroundColor: StrollColors.primaryPurple,      child: FadeIn(        delay: const Duration(milliseconds: 1500),        child: Center(          child: ScaleTransition(              scale: _animation,              child: Text(                'Stroll',                style: StrollFont.w700.copyWith(                  fontSize: 60.sp,                  color: StrollColors.white,                ),              )),        ),      ),    );  }}