import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:stroll/features/task/task.dart';import 'package:stroll/shared/shared.dart';class TaskScreen extends StatelessWidget {  const TaskScreen({super.key});  @override  Widget build(BuildContext context) {    final answers = [      'The peace in the early mornings',      'The magical golden hours',      'Wind-down time after dinners',      'The serenity past midnight',    ];    return PageWrapper(      padding: EdgeInsets.only(bottom: MediaQuery.of(context).padding.bottom),      backgroundColor: StrollColors.black,      child: Stack(        children: [          Image.asset(            'assets/images/video.png',            fit: BoxFit.cover,          ),          _buildBgGradient(),          const Header(),          Positioned(            bottom: 0,            left: 0,            right: 0,            child: QuestionBody(              answers: answers,              question: 'What is your favorite time of the day?',            ),          ),        ],      ),    );  }  Widget _buildBgGradient() => Container(        decoration: BoxDecoration(          gradient: LinearGradient(            stops: [0.0, 0.26.w, 0.9.w],            colors: const [              Colors.transparent,              StrollColors.black,              StrollColors.black,            ],            begin: Alignment.center,            end: Alignment.bottomCenter,          ),        ),      );}