import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:flutter_svg/flutter_svg.dart';import 'package:stroll/shared/shared.dart';class QuestionBody extends StatelessWidget {  QuestionBody({    super.key,    required this.answers,    required this.question,    this.onAnswerSelected,  });  final List<String> answers;  final String question;  final ValueChanged<String>? onAnswerSelected;  final _answerNotifier = ValueNotifier<int?>(null);  @override  Widget build(BuildContext context) {    return Column(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Container(            padding: REdgeInsets.only(left: 27),            height: 76.h,            width: double.maxFinite,            child: Stack(              alignment: Alignment.topLeft,              children: [                Container(                  width: 100.w,                  height: 20.h,                  margin: REdgeInsets.only(left: 32),                  padding: REdgeInsets.only(right: 8),                  decoration: BoxDecoration(                    borderRadius: BorderRadius.circular(20),                    color: const Color(0xff121518).withValues(alpha: 0.9),                  ),                  alignment: Alignment.centerRight,                  child: Text(                    'Angelina, 28',                    style: StrollFont.w700.copyWith(                      fontSize: 11.sp,                      height: 13.h / 11.sp,                      color: StrollColors.white,                    ),                  ),                ),                Container(                  height: 60.w,                  width: 60.w,                  decoration: BoxDecoration(                    shape: BoxShape.circle,                    border: Border.all(                      width: 7.w,                      color: const Color(0xff121518).withValues(alpha: 0.9),                    ),                  ),                  child: Image.asset('assets/images/place_holder.png'),                ),                Positioned(                    top: 29,                    left: 69,                    right: 32,                    child: SizedBox(                        width: 247.w,                        child: Text(                          question,                          style: StrollFont.w700.copyWith(                            fontSize: 20.sp,                            height: 20.h / 20.sp,                            color: StrollColors.white,                          ),                        )))              ],            )),        9.verticalSpace,        Center(            child: Text(          '“Mine is definitely the peace in the morning.”',          style: StrollFont.w400.copyWith(            fontSize: 12.sp,            height: 14.4.h / 12.sp,            fontStyle: FontStyle.italic,            color: StrollColors.primaryPurpleLighter.withValues(alpha: 0.7),          ),        )),        14.verticalSpace,        Center(            child: Wrap(          spacing: 12.w,          runSpacing: 12.h,          children: List.generate(            answers.length,            (index) {              final answer = answers[index];              return ValueListenableBuilder(                  valueListenable: _answerNotifier,                  builder: (context, val, __) {                    final selected = val == index;                    return InkWell(                      onTap: () {                        if (_answerNotifier.value == index) {                          _answerNotifier.value = null;                          return;                        }                        _answerNotifier.value = index;                        onAnswerSelected?.call(answer);                      },                      child: Container(                        padding: REdgeInsets.symmetric(horizontal: 9, vertical: 13.5),                        decoration: BoxDecoration(                          borderRadius: BorderRadius.circular(12.r),                          color: const Color(0xff232A2E).withValues(alpha: 0.5),                          boxShadow: [                            BoxShadow(                              color: StrollColors.black.withValues(alpha: 0.3),                              offset: const Offset(-1, -1),                              blurRadius: 2,                            ),                          ],                          border: selected                              ? Border.all(                                  width: 2.w,                                  color: StrollColors.primaryPurple,                                  strokeAlign: BorderSide.strokeAlignInside,                                )                              : null,                        ),                        child: Row(                          mainAxisSize: MainAxisSize.min,                          children: [                            Container(                              padding: REdgeInsets.all(6),                              decoration: BoxDecoration(                                shape: BoxShape.circle,                                color: selected ? StrollColors.primaryPurple : null,                                border: selected                                    ? null                                    : Border.all(                                        width: 1,                                        color: StrollColors.white,                                      ),                              ),                              child: Text(                                'A,B,C,D'.split(',')[index],                                style: StrollFont.w400.copyWith(                                  fontSize: 12.sp,                                  height: 14.4.h / 12.sp,                                  color: StrollColors.white,                                ),                              ),                            ),                            9.horizontalSpace,                            SizedBox(                              width: 118.w,                              child: Text(                                answer,                                style: StrollFont.w400.copyWith(                                  fontSize: 14.sp,                                  height: 14.4.h / 14.sp,                                  color: StrollColors.white,                                ),                              ),                            ),                          ],                        ),                      ),                    );                  });            },          ),        )),        11.verticalSpace,        Padding(          padding: REdgeInsets.symmetric(horizontal: 15),          child: Row(            children: [              Expanded(                child: Text(                  'Pick your option.\nSee who has a similar mind.',                  style: StrollFont.w400.copyWith(                    fontSize: 12.sp,                    height: 14.4.h / 14.sp,                    color: StrollColors.white,                  ),                ),              ),              SvgPicture.asset(                'assets/icons/microphone.svg',                height: 48.h,              ),              6.horizontalSpace,              SvgPicture.asset(                'assets/icons/next.svg',                height: 48.h,              ),            ],          ),        ),        7.verticalSpace,      ],    );  }}